import { useState } from "react";
import { useMutation } from "@tanstack/react-query";

export const CreateGroupModal = ({ users, onClose }) => {
  const [name, setName] = useState("");
  const [selected, setSelected] = useState([]);
  const mutation = useMutation(createGroup);

  return (
    <div className="modal">
      <input
        placeholder="Group name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      {users.map((u) => (
        <label key={u.auth_id}>
          <input
            type="checkbox"
            onChange={() =>
              setSelected((prev) =>
                prev.includes(u.auth_id)
                  ? prev.filter((i) => i !== u.auth_id)
                  : [...prev, u.auth_id]
              )
            }
          />
          {u.user_name}
        </label>
      ))}

      <button
        onClick={() =>
          mutation.mutate({ name, members: selected })
        }
      >
        Create
      </button>
    </div>
  );
};
