import { useSelector } from "react-redux";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { fetchUsers, getConversation } from "../Tanstack/Chatlist";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { useSocket } from "../../socket/socketContext";
import { CreateGroupModal } from "./groupModal";

const SideBar = () => {
  const currentUser = useSelector((state) => state.auth.user);
  const navigate = useNavigate();
  const [openingUserId, setOpeningUserId] = useState(null);

  const socket = useSocket();
  const queryClient = useQueryClient();

  const { data: users = [], isLoading } = useQuery({
    queryKey: ["users"],
    queryFn: fetchUsers,
  });

  const openChatMutation = useMutation({
    mutationFn: getConversation,
    onSuccess: (conver) => {
      navigate(`/chat/${conver.conversation_id}`);
      setOpeningUserId(null);
    },
    onError: () => {
      setOpeningUserId(null);
      alert("Failed to open chat");
    },
  });

  const openChat = (userId) => {
    if (openingUserId) return;
    setOpeningUserId(userId);
    openChatMutation.mutate(userId);
  };

  useEffect(() => {
    if (!socket) return;

    const handleUserCreated = (user) => {
      queryClient.setQueryData(["users"], (old = []) => {
        if (old.some((u) => u.auth_id === user.auth_id)) return old;
        return [...old, user];
      });
    };

    socket.on("user_created", handleUserCreated);

    return () => {
      socket.off("user_created", handleUserCreated);
    };
  }, [socket, queryClient]);

  if (isLoading) {
    return (
      <aside className="w-64 h-full flex items-center justify-center">
        <span className="text-sm text-gray-400">Loading users...</span>
      </aside>
    );
  }

  const filteredUsers = users.filter((u) => u.auth_id !== currentUser?.auth_id);

  return (
    <>
      <aside className="w-64 h-full border-r bg-slate-900 text-white flex flex-col">
        {/* HEADER */}
        <div className="p-4 border-b font-semibold flex justify-between items-center">
          Chats
          <button
            onClick={() => setShowModal(true)}
            className="text-sm bg-emerald-600 px-2 py-1 rounded"
          >
            + Group
          </button>
        </div>

        {/* GROUPS */}
        <div className="px-4 py-2 text-xs text-gray-400">GROUPS</div>
        <ul>
          {groups.map((g) => (
            <li
              key={g.id}
              onClick={() => navigate(`/chat/group/${g.conversation_id}`)}
              className="px-4 py-2 cursor-pointer hover:bg-slate-800"
            >
              ðŸ‘¥ {g.name}
            </li>
          ))}
        </ul>

        {/* USERS */}
        <div className="px-4 py-2 text-xs text-gray-400 mt-3">
          DIRECT MESSAGES
        </div>
        <ul className="flex-1 overflow-y-auto">
          {users.map((u) => (
            <li
              key={u.auth_id}
              onClick={() => navigate(`/chat/${u.auth_id}`)}
              className="px-4 py-2 cursor-pointer hover:bg-slate-800"
            >
              {u.user_name}
            </li>
          ))}
        </ul>
      </aside>

      {/* MODAL */}
      {showModal && (
        <CreateGroupModal
          users={users}
          onClose={() => setShowModal(false)}
        />
      )}
    </>
  );
};

export default Sidebar;
