import { useParams } from "react-router-dom";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import { useSocket } from "../../socket/socketContext";
import { Paperclip } from "lucide-react";

import {
  fetchMessages,
  sendMessage,
  fetchConversationMeta,
  fetchUserById,
} from "../Tanstack/Chatlist";

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const socket = useSocket();
  const currentUser = useSelector((state) => state.auth.user);

  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const [text, setText] = useState("");
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);

  const bottomRef = useRef(null);
  const fileInputRef = useRef(null);

  /* ================= CONVERSATION META ================= */
  const { data: convo } = useQuery({
    queryKey: ["conversation-meta", conversationId],
    queryFn: () => fetchConversationMeta(conversationId),
    enabled: !!conversationId,
  });

  const { data: receiver } = useQuery({
    queryKey: ["user", convo?.receiver_id],
    queryFn: () => fetchUserById(convo.receiver_id),
    enabled: convo?.type === "private" && !!convo?.receiver_id,
  });

  /* ================= FETCH MESSAGES (ON CHAT CHANGE ONLY) ================= */
  useEffect(() => {
    if (!conversationId) return;

    setIsLoading(true);
    setMessages([]);

    fetchMessages(conversationId)
      .then((data) => setMessages(data))
      .finally(() => setIsLoading(false));
  }, [conversationId]);

  /* ================= SOCKET RECEIVE ================= */
  useEffect(() => {
    if (!conversationId) return;

    const handler = (message) => {
      // ðŸ” HARD FILTER â€“ prevents cross-chat messages
      if (message.conversation_id !== Number(conversationId)) return;

      // ðŸ›‘ Prevent duplicates
      setMessages((prev) => {
        if (prev.some((m) => m.id === message.id)) return prev;
        return [...prev, message];
      });
    };

    socket.on("receive_message", handler);

    return () => {
      socket.off("receive_message", handler);
    };
  }, [conversationId, socket]);

  /* ================= SEND MESSAGE ================= */
  const sendMessageMutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: (message) => {
      // Push immediately (no refetch)
      setMessages((prev) => {
        if (prev.some((m) => m.id === message.id)) return prev;
        return [...prev, message];
      });

      setText("");
      setImages([]);
      setPreviews([]);
      fileInputRef.current.value = null;
    },
  });

  /* ================= AUTO SCROLL ================= */
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  /* ================= LOADING ================= */
  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--text-muted)" }}>Loading chatâ€¦</span>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col">
      {/* ================= HEADER ================= */}
      <div
        className="px-4 py-3 border-b font-semibold flex items-center gap-3"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
          color: "var(--text-main)",
        }}
      >
        <div
          className="w-9 h-9 rounded-full flex items-center justify-center font-semibold"
          style={{
            backgroundColor: "var(--accent-secondary)",
            color: "#020617",
          }}
        >
          {convo?.type === "group"
            ? convo.group_name?.charAt(0)
            : receiver?.user_name?.charAt(0) || "P"}
        </div>

        <span>
          {convo?.type === "private"
            ? receiver?.user_name || "Chat"
            : convo?.group_name}
        </span>
      </div>

      {/* ================= MESSAGES ================= */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.length === 0 ? (
          <p
            className="text-center text-sm mt-10"
            style={{ color: "var(--text-muted)" }}
          >
            No messages yet. Say hello ðŸ‘‹
          </p>
        ) : (
          messages.map((msg) => {
            const isMe = msg.sender_id === currentUser?.auth_id;

            return (
              <div
                key={msg.id}
                className={`flex ${isMe ? "justify-end" : "justify-start"}`}
              >
                <div
                  className="inline-block max-w-[60%] px-4 py-2 rounded-lg text-sm break-words"
                  style={{
                    backgroundColor: isMe
                      ? "var(--accent-primary)"
                      : "var(--bg-input)",
                    color: isMe ? "#020617" : "var(--text-main)",
                  }}
                >
                  {msg.images?.length > 0 && (
                    <img
                      src={`http://localhost:3001${msg.images[0]}`}
                      alt="sent"
                      className="max-w-60 max-h-60 object-contain rounded-lg mb-2"
                    />
                  )}

                  {msg.text && <p>{msg.text}</p>}
                </div>
              </div>
            );
          })
        )}
        <div ref={bottomRef} />
      </div>

      {/* ================= IMAGE PREVIEWS ================= */}
      {previews.length > 0 && (
        <div className="px-3 py-2 border-t flex gap-3 flex-wrap">
          {previews.map((src, idx) => (
            <div key={idx} className="relative">
              <img
                src={src}
                alt="preview"
                className="max-w-40 max-h-40 rounded-lg"
              />
            </div>
          ))}
        </div>
      )}

      {/* ================= INPUT ================= */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!text.trim() && images.length === 0) return;

          sendMessageMutation.mutate({
            conversationId,
            text,
            images,
          });
        }}
        className="p-3 border-t flex gap-2"
      >
        <input
          type="file"
          multiple
          accept="image/*"
          ref={fileInputRef}
          hidden
          onChange={(e) => {
            const files = Array.from(e.target.files);
            setImages(files);
            setPreviews(files.map((f) => URL.createObjectURL(f)));
          }}
        />

        <button
          type="button"
          onClick={() => fileInputRef.current.click()}
        >
          <Paperclip size={18} />
        </button>

        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 px-3 py-2 rounded-md"
        />

        <button
          type="submit"
          disabled={sendMessageMutation.isLoading}
          className="px-4 py-2 rounded-md"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatDashboard;
