import { useParams } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import { useSocket } from "../../socket/socketContext";
import { fetchMessages, sendMessage } from "../Tanstack/Chatlist";

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const socket = useSocket();
  const queryClient = useQueryClient();
  const currentUser = useSelector((s) => s.auth.user);

  const [text, setText] = useState("");
  const [image, setImage] = useState(null);
  const bottomRef = useRef(null);

  const { data: messages = [] } = useQuery({
    queryKey: ["messages", conversationId],
    queryFn: () => fetchMessages(conversationId),
    enabled: !!conversationId,
  });

  useEffect(() => {
    if (!socket || !conversationId) return;

    socket.emit("join_conversation", conversationId);

    const handleReceive = (message) => {
      queryClient.setQueryData(
        ["messages", conversationId],
        (old = []) => {
          if (old.some((m) => m.id === message.id)) return old;
          return [...old, message];
        }
      );
    };

    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [socket, conversationId, queryClient]);

  const mutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: () => {
      setText("");
      setImage(null);
    },
  });

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="flex-1 flex flex-col">
      <div className="flex-1 p-4 overflow-y-auto space-y-3">
        {messages.map((msg) => {
          const isMe = msg.sender_id === currentUser.auth_id;
          return (
            <div key={msg.id} className={`flex ${isMe ? "justify-end" : "justify-start"}`}>
              <div className="max-w-xs p-2 rounded-lg bg-gray-200">
                {msg.type === "image" ? (
                  <img
                    src={`http://localhost:3001${msg.image_url}`}
                    className="rounded-lg"
                    alt="sent"
                  />
                ) : (
                  msg.text
                )}
              </div>
            </div>
          );
        })}
        <div ref={bottomRef} />
      </div>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          mutation.mutate({ conversationId, text, image });
        }}
        className="p-3 flex gap-2 border-t"
      >
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="flex-1 border px-3 py-2"
          placeholder="Type a message..."
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatDashboard;
