import { useSelector } from "react-redux";
import { useQuery } from "@tanstack/react-query";
import { fetchUsers,getConversation } from "../Tanstack/Chatlist";
import { useNavigate } from "react-router-dom";

const SideBar = () => {
  const currentUser = useSelector((state) => state.auth.user);

  const { data: users = [], isLoading } = useQuery({
    queryKey: ["users"],
    queryFn: fetchUsers,
  });
  const navigate = useNavigate();

  const openChat = async (userId) => {
    const convo = await getConversation(userId);
    navigate(`/chat/${convo.id}`);
  };

  if (isLoading) {
    return (
      <aside className="w-64 h-full flex items-center justify-center">
        <span className="text-sm text-gray-400">Loading users...</span>
      </aside>
    );
  }

  const filteredUsers = users.filter(
    (user) => user.auth_id !== currentUser?.auth_id,
  );

  return (
    <aside className="w-64 border-r">
      <div className="p-4 font-semibold">Conversations</div>

      {users
        .filter((u) => u.auth_id !== me?.auth_id)
        .map((user) => (
          <div
            key={user.auth_id}
            onClick={() => openChat(user.auth_id)}
            className="px-4 py-3 cursor-pointer hover:bg-gray-700"
          >
            {user.user_name}
          </div>
        ))}
    </aside>
  );
};

export default SideBar;
