import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { createGroup } from "../api/chatApi";

const CreateGroupModal = ({ users, onClose }) => {
  const [name, setName] = useState("");
  const [selected, setSelected] = useState([]);

  const queryClient = useQueryClient();

  const mutation = useMutation({
    mutationFn: createGroup,
    onSuccess: () => {
      queryClient.invalidateQueries(["groups"]);
      onClose();
    },
  });

  const toggleUser = (id) => {
    setSelected((prev) =>
      prev.includes(id)
        ? prev.filter((x) => x !== id)
        : [...prev, id]
    );
  };

  return (
    <div className="fixed inset-0 bg-black/60 flex items-center justify-center z-50">
      <div className="bg-slate-900 w-96 p-4 rounded text-white">
        <h2 className="font-semibold mb-3">Create Group</h2>

        {/* GROUP NAME */}
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Group name"
          className="w-full mb-3 p-2 rounded bg-slate-800 outline-none"
        />

        {/* USERS */}
        <div className="max-h-48 overflow-y-auto border rounded p-2">
          {users.map((u) => (
            <label
              key={u.auth_id}
              className="flex items-center gap-2 text-sm mb-1 cursor-pointer"
            >
              <input
                type="checkbox"
                checked={selected.includes(u.auth_id)}
                onChange={() => toggleUser(u.auth_id)}
              />
              {u.user_name}
            </label>
          ))}
        </div>

        {/* ACTIONS */}
        <div className="flex justify-end gap-2 mt-4">
          <button
            onClick={onClose}
            className="px-3 py-1 bg-gray-600 rounded"
          >
            Cancel
          </button>
          <button
            onClick={() =>
              mutation.mutate({
                name,
                members: selected,
              })
            }
            disabled={!name || selected.length === 0}
            className="px-3 py-1 bg-emerald-600 rounded"
          >
            Create
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateGroupModal;
