import { useParams } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { fetchMessages, sendMessage } from "../Tanstack/Chatlist";
import { useState } from "react";
import { useSelector } from "react-redux";

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const queryClient = useQueryClient();
  const currentUser = useSelector((state) => state.auth.user);
  const [text, setText] = useState("");

  if (!conversationId) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--text-muted)" }}>
          Select a conversation to start chatting ðŸ’¬
        </span>
      </div>
    );
  }

  const { data: messages = [] } = useQuery({
    queryKey: ["messages", conversationId],
    queryFn: () => fetchMessages(conversationId),
    enabled: !!conversationId,
    retry: false,
  });

  const mutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: () => {
      queryClient.invalidateQueries(["messages", conversationId]);
      setText("");
    },
  });

  return (
    <div className="flex-1 flex flex-col">
      <div className="flex-1 p-4 overflow-y-auto space-y-3">
        {messages.map((msg) => {
          const isMe = msg.sender_id === currentUser?.auth_id;

          return (
            <div
              key={msg.id}
              className={`max-w-xs px-4 py-2 rounded ${
                isMe ? "ml-auto" : "mr-auto"
              }`}
            >
              {msg.text}
            </div>
          );
        })}
      </div>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!text.trim()) return;
          mutation.mutate({ conversationId, text });
        }}
        className="p-3 border-t flex gap-2"
      >
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="flex-1 px-3 py-2"
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatDashboard;
