import { useParams } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import { useSocket } from "../../socket/socketContext";
import { Paperclip } from "lucide-react";
import {
  fetchMessages,
  sendMessage,
  fetchConversationMeta,
  fetchUserById,
} from "../Tanstack/Chatlist";

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const currentUser = useSelector((state) => state.auth.user);
  const queryClient = useQueryClient();
  const socket = useSocket();

  const [text, setText] = useState("");
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);

  const bottomRef = useRef(null);
  const fileInputRef = useRef(null);

  /* ---------------- QUERIES ---------------- */

  const { data: convo } = useQuery({
    queryKey: ["conversation-meta", conversationId],
    queryFn: () => fetchConversationMeta(conversationId),
    enabled: !!conversationId,
  });

  const { data: receiver } = useQuery({
    queryKey: ["user", convo?.receiver_id],
    queryFn: () => fetchUserById(convo.receiver_id),
    enabled: !!convo?.receiver_id,
  });

  const { data: messages = [], isLoading } = useQuery({
    queryKey: ["messages", conversationId],
    queryFn: () => fetchMessages(conversationId),
    enabled: !!conversationId,
  });

  /* ---------------- SOCKET ---------------- */

  useEffect(() => {
    if (!conversationId || !socket) return;

    socket.emit("join_conversation", conversationId);

    const handleReceiveMessage = () => {
      queryClient.invalidateQueries({
        queryKey: ["messages", conversationId],
      });
    };

    socket.on("receive_message", handleReceiveMessage);

    return () => {
      socket.off("receive_message", handleReceiveMessage);
    };
  }, [conversationId, socket, queryClient]);

  /* ---------------- SEND MESSAGE ---------------- */

  const sendMessageMutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: () => {
      setText("");
      setImages([]);
      setPreviews([]);
      if (fileInputRef.current) fileInputRef.current.value = null;
    },
  });

  /* ---------------- AUTO SCROLL ---------------- */

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  /* ---------------- CLEANUP PREVIEWS ---------------- */

  useEffect(() => {
    return () => {
      previews.forEach((url) => URL.revokeObjectURL(url));
    };
  }, [previews]);

  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--text-muted)" }}>Loading chatâ€¦</span>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col">
      {/* ================= HEADER ================= */}
      <div
        className="px-4 py-3 border-b font-semibold flex items-center gap-3"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
          color: "var(--text-main)",
        }}
      >
        <div
          className="w-9 h-9 rounded-full flex items-center justify-center font-semibold"
          style={{
            backgroundColor: "var(--accent-secondary)",
            color: "#020617",
          }}
        >
          {receiver?.user_name?.charAt(0) || "?"}
        </div>
        <span>{receiver?.user_name || "Chat"}</span>
      </div>

      {/* ================= MESSAGES ================= */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.length === 0 ? (
          <p className="text-center text-sm mt-10" style={{ color: "var(--text-muted)" }}>
            No messages yet. Say hello ðŸ‘‹
          </p>
        ) : (
          messages.map((msg) => {
            const isMe = msg.sender_id === currentUser?.auth_id;

            return (
              <div
                key={msg.id}
                className={`flex ${isMe ? "justify-end" : "justify-start"}`}
              >
                <div
                  className="inline-block max-w-[60%] px-4 py-2 rounded-lg text-sm space-y-2"
                  style={{
                    backgroundColor: isMe
                      ? "var(--accent-primary)"
                      : "var(--bg-input)",
                    color: isMe ? "#020617" : "var(--text-main)",
                  }}
                >
                  {msg.text && <p>{msg.text}</p>}

                  {msg.images?.length > 0 && (
                    <div className="grid grid-cols-2 gap-2">
                      {msg.images.map((img, idx) => (
                        <img
                          key={idx}
                          src={`http://localhost:3001${img}`}
                          alt="sent"
                          className="max-w-60 max-h-60 object-contain rounded-lg"
                        />
                      ))}
                    </div>
                  )}
                </div>
              </div>
            );
          })
        )}
        <div ref={bottomRef} />
      </div>

      {/* ================= IMAGE PREVIEWS ================= */}
      {previews.length > 0 && (
        <div
          className="px-3 py-2 border-t flex gap-3 flex-wrap"
          style={{ backgroundColor: "var(--bg-card)" }}
        >
          {previews.map((src, idx) => (
            <div key={idx} className="relative">
              <img
                src={src}
                alt="preview"
                className="max-w-40 max-h-40 object-cover rounded-lg border"
              />
              <button
                type="button"
                onClick={() => {
                  const newImages = images.filter((_, i) => i !== idx);
                  const newPreviews = previews.filter((_, i) => i !== idx);

                  setImages(newImages);
                  setPreviews(newPreviews);

                  if (newImages.length === 0 && fileInputRef.current) {
                    fileInputRef.current.value = null;
                  }
                }}
                className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-1"
              >
                âœ•
              </button>
            </div>
          ))}
        </div>
      )}

      {/* ================= INPUT ================= */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!text.trim() && images.length === 0) return;

          sendMessageMutation.mutate({
            conversationId,
            text,
            images,
          });
        }}
        className="p-3 border-t flex gap-2"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
        }}
      >
        {/* HIDDEN FILE INPUT */}
        <input
          type="file"
          accept="image/*"
          multiple
          ref={fileInputRef}
          hidden
          onChange={(e) => {
            const files = Array.from(e.target.files);
            setImages(files);
            setPreviews(files.map((f) => URL.createObjectURL(f)));
          }}
        />

        {/* INPUT WITH PAPERCLIP */}
        <div className="relative flex-1">
          <button
            type="button"
            onClick={() => fileInputRef.current.click()}
            className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-200"
            title="Attach images"
          >
            <Paperclip size={18} />
          </button>

          <input
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Type a message..."
            className="w-full pl-10 pr-4 py-2 rounded-md outline-none"
            style={{
              backgroundColor: "var(--bg-input)",
              color: "var(--text-main)",
              border: "1px solid var(--border-input)",
            }}
          />
        </div>

        <button
          type="submit"
          disabled={sendMessageMutation.isLoading}
          className="px-4 py-2 rounded-md font-medium"
          style={{
            backgroundColor: "var(--accent-primary)",
            color: "#020617",
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatDashboard;
