import { useParams } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import { useSocket } from "../../socket/socketContext";
import { Paperclip } from "lucide-react";
import {
  fetchMessages,
  sendMessage,
  fetchConversationMeta,
  fetchUserById,
} from "../Tanstack/Chatlist";

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const currentUser = useSelector((state) => state.auth.user);
  const queryClient = useQueryClient();
  const socket = useSocket();

  const [text, setText] = useState("");
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [liveMessages, setLiveMessages] = useState([]);

  const bottomRef = useRef(null);
  const fileInputRef = useRef(null);

  /* ================= CONVERSATION META ================= */
  const { data: convo } = useQuery({
    queryKey: ["conversation-meta", conversationId],
    queryFn: () => fetchConversationMeta(conversationId),
    enabled: !!conversationId,
  });

  const { data: receiver } = useQuery({
    queryKey: ["user", convo?.receiver_id],
    queryFn: () => fetchUserById(convo.receiver_id),
    enabled: convo?.type === "private" && !!convo?.receiver_id,
  });

  /* ================= FETCH MESSAGES (ONCE PER CHAT) ================= */
  const { data: messages = [], isLoading } = useQuery({
    queryKey: ["messages", conversationId],
    queryFn: () => fetchMessages(conversationId),
    enabled: !!conversationId,
  });

  /* ================= RESET LIVE MESSAGES ON CHAT CHANGE ================= */
  useEffect(() => {
    setLiveMessages([]);
  }, [conversationId]);

  /* ================= SOCKET JOIN & RECEIVE ================= */
  useEffect(() => {
  if (!conversationId || !socket) return;
  if (!socket.connected) return;

  socket.emit("join_conversation", conversationId);

  const handler = (message) => {
    if (message.conversation_id !== Number(conversationId)) return;

    setLiveMessages((prev) => {
      if (prev.some((m) => m.id === message.id)) return prev;
      return [...prev, message];
    });
  };

  socket.on("receive_message", handler);

  return () => {
    socket.off("receive_message", handler);
  };
}, [conversationId, socket, socket?.connected]);


  /* ================= SEND MESSAGE ================= */
  const sendMessageMutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: (message) => {
      // append instantly for sender
      setLiveMessages((prev) => [...prev, message]);

      setText("");
      setImages([]);
      setPreviews([]);
      fileInputRef.current.value = null;
    },
  });

  /* ================= AUTO SCROLL ================= */
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, liveMessages]);

  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--text-muted)" }}>Loading chatâ€¦</span>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col">
      {/* ================= HEADER ================= */}
      <div
        className="px-4 py-3 border-b font-semibold flex items-center gap-3"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
          color: "var(--text-main)",
        }}
      >
        <div
          className="w-9 h-9 rounded-full flex items-center justify-center font-semibold"
          style={{
            backgroundColor: "var(--accent-secondary)",
            color: "#020617",
          }}
        >
          {convo?.type === "group"
            ? convo.group_name?.charAt(0)
            : receiver?.user_name?.charAt(0) || "P"}
        </div>
        <span>
          {convo?.type === "private"
            ? receiver?.user_name || "Chat"
            : convo?.group_name}
        </span>
      </div>

      {/* ================= MESSAGES ================= */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.length === 0 && liveMessages.length === 0 ? (
          <p
            className="text-center text-sm mt-10"
            style={{ color: "var(--text-muted)" }}
          >
            No messages yet. Say hello ðŸ‘‹
          </p>
        ) : (
          <>
            {/* OLD MESSAGES (API) */}
            {messages.map((msg) => {
              const isMe = msg.sender_id === currentUser?.auth_id;

              return (
                <div
                  key={msg.id}
                  className={`flex ${isMe ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className="inline-block max-w-[60%] px-4 py-2 rounded-lg text-sm break-words"
                    style={{
                      backgroundColor: isMe
                        ? "var(--accent-primary)"
                        : "var(--bg-input)",
                      color: isMe ? "#020617" : "var(--text-main)",
                    }}
                  >
                    {msg.text && <p>{msg.text}</p>}
                  </div>
                </div>
              );
            })}

            {/* HERE â†’ LIVE SOCKET MESSAGES */}
            {liveMessages.map((msg) => {
              const isMe = msg.sender_id === currentUser?.auth_id;

              return (
                <div
                  key={`live-${msg.id}`}
                  className={`flex ${isMe ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className="inline-block max-w-[60%] px-4 py-2 rounded-lg text-sm break-words"
                    style={{
                      backgroundColor: isMe
                        ? "var(--accent-primary)"
                        : "var(--bg-input)",
                      color: isMe ? "#020617" : "var(--text-main)",
                    }}
                  >
                    {msg.text && <p>{msg.text}</p>}
                  </div>
                </div>
              );
            })}
          </>
        )}
        <div ref={bottomRef} />
      </div>

      {/* ================= INPUT ================= */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (!text.trim() && images.length === 0) return;

          sendMessageMutation.mutate({
            conversationId,
            text,
            images,
          });
        }}
        className="p-3 border-t flex gap-2"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
        }}
      >
        <input
          type="file"
          accept="image/*"
          multiple
          ref={fileInputRef}
          style={{ display: "none" }}
          onChange={(e) => {
            const files = Array.from(e.target.files);
            setImages(files);
            setPreviews(files.map((f) => URL.createObjectURL(f)));
          }}
        />

        <div className="relative flex-1">
          <button
            type="button"
            onClick={() => fileInputRef.current.click()}
            className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400"
          >
            <Paperclip size={18} />
          </button>

          <input
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Type a message..."
            className="w-full pl-10 pr-4 py-2 rounded-md outline-none"
            style={{
              backgroundColor: "var(--bg-input)",
              color: "var(--text-main)",
            }}
          />
        </div>

        <button
          type="submit"
          disabled={sendMessageMutation.isLoading}
          className="px-4 py-2 rounded-md font-medium"
          style={{
            backgroundColor: "var(--accent-primary)",
            color: "#020617",
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatDashboard;
