import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useSocket } from "../socket/socketContext";
import { fetchUsers, fetchGroups, createGroup } from "../api/chatApi";
import CreateGroupModal from "./CreateGroupModal";

const Sidebar = () => {
  const navigate = useNavigate();
  const socket = useSocket();
  const queryClient = useQueryClient();

  const [showModal, setShowModal] = useState(false);

  /* ---------- FETCH USERS ---------- */
  const { data: users = [] } = useQuery({
    queryKey: ["users"],
    queryFn: fetchUsers,
  });

  /* ---------- FETCH GROUPS ---------- */
  const { data: groups = [] } = useQuery({
    queryKey: ["groups"],
    queryFn: fetchGroups,
  });

  /* ---------- SOCKET LISTENER ---------- */
  useEffect(() => {
    if (!socket) return;

    socket.on("group_created", () => {
      queryClient.invalidateQueries(["groups"]);
    });

    return () => socket.off("group_created");
  }, [socket, queryClient]);

  return (
    <>
      <aside className="w-64 h-full border-r bg-slate-900 text-white flex flex-col">
        {/* HEADER */}
        <div className="p-4 border-b font-semibold flex justify-between items-center">
          Chats
          <button
            onClick={() => setShowModal(true)}
            className="text-sm bg-emerald-600 px-2 py-1 rounded"
          >
            + Group
          </button>
        </div>

        {/* GROUPS */}
        <div className="px-4 py-2 text-xs text-gray-400">GROUPS</div>
        <ul>
          {groups.map((g) => (
            <li
              key={g.id}
              onClick={() => navigate(`/chat/group/${g.conversation_id}`)}
              className="px-4 py-2 cursor-pointer hover:bg-slate-800"
            >
              ðŸ‘¥ {g.name}
            </li>
          ))}
        </ul>

        {/* USERS */}
        <div className="px-4 py-2 text-xs text-gray-400 mt-3">
          DIRECT MESSAGES
        </div>
        <ul className="flex-1 overflow-y-auto">
          {users.map((u) => (
            <li
              key={u.auth_id}
              onClick={() => navigate(`/chat/${u.auth_id}`)}
              className="px-4 py-2 cursor-pointer hover:bg-slate-800"
            >
              {u.user_name}
            </li>
          ))}
        </ul>
      </aside>

      {/* MODAL */}
      {showModal && (
        <CreateGroupModal
          users={users}
          onClose={() => setShowModal(false)}
        />
      )}
    </>
  );
};

export default Sidebar;
