import { useParams } from "react-router-dom";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useSelector } from "react-redux";
import { useState, useEffect, useRef } from "react";
import { fetchMessages,sendMessage } from "../Tanstack/Chatlist";


/* ---------------- COMPONENT ---------------- */

const ChatDashboard = () => {
  const { conversationId } = useParams();
  const queryClient = useQueryClient();
  const currentUser = useSelector((state) => state.auth.user);

  const [text, setText] = useState("");
  const messagesEndRef = useRef(null);

  /* Fetch messages */
  const {
    data: messages = [],
    isLoading,
    isError,
  } = useQuery({
    queryKey: ["messages", conversationId],
    queryFn: () => fetchMessages(conversationId),
    enabled: !!conversationId,
  });

  /* Send message mutation */
  const sendMutation = useMutation({
    mutationFn: sendMessage,
    onSuccess: () => {
      queryClient.invalidateQueries({queryKey:["messages", conversationId]});
      setText("");
    },
  });

  /* Auto-scroll to bottom on new message */
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  /* Submit handler */
  const handleSend = (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    sendMutation.mutate({
      conversationId,
      text,
    });
  };

  /* Loading */
  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--text-muted)" }}>
          Loading conversation...
        </span>
      </div>
    );
  }

  /* Error */
  if (isError) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <span style={{ color: "var(--danger)" }}>
          Failed to load messages
        </span>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col">
      {/* Header */}
      <div
        className="px-4 py-3 border-b font-semibold"
        style={{
          borderColor: "var(--border-main)",
          color: "var(--text-main)",
          backgroundColor: "var(--bg-card)",
        }}
      >
        Conversation
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.length === 0 ? (
          <p
            className="text-center mt-10 text-sm"
            style={{ color: "var(--text-muted)" }}
          >
            No messages yet. Say hello ðŸ‘‹
          </p>
        ) : (
          messages.map((msg) => {
            const isMe = msg.sender_id === currentUser?.auth_id;

            return (
              <div
                key={msg.id}
                className={`max-w-xs px-4 py-2 rounded-lg text-sm ${
                  isMe ? "ml-auto" : "mr-auto"
                }`}
                style={{
                  backgroundColor: isMe
                    ? "var(--accent-primary)"
                    : "var(--bg-input)",
                  color: isMe ? "#020617" : "var(--text-main)",
                }}
              >
                {msg.text}
              </div>
            );
          })
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <form
        onSubmit={handleSend}
        className="p-3 border-t flex gap-2"
        style={{
          borderColor: "var(--border-main)",
          backgroundColor: "var(--bg-card)",
        }}
      >
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 px-4 py-2 rounded-md outline-none"
          style={{
            backgroundColor: "var(--bg-input)",
            color: "var(--text-main)",
            border: "1px solid var(--border-input)",
          }}
        />
        <button
          type="submit"
          disabled={sendMutation.isLoading}
          className="px-4 py-2 rounded-md font-medium transition"
          style={{
            backgroundColor: "var(--accent-primary)",
            color: "#020617",
            opacity: sendMutation.isLoading ? 0.7 : 1,
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatDashboard;
