import { useParams } from "react-router-dom";

const dummyChats = {
  1: [
    { from: "them", text: "Hey ðŸ‘‹" },
    { from: "me", text: "Hi! How are you?" },
    { from: "them", text: "All good ðŸ˜Š" },
  ],
  2: [
    { from: "them", text: "Did you check the update?" },
    { from: "me", text: "Yes, looks good!" },
  ],
  3: [
    { from: "them", text: "Meeting at 6?" },
    { from: "me", text: "Sure ðŸ‘" },
  ],
};

const ChatDashboard = () => {
  const { userId } = useParams();
  const messages = dummyChats[userId] || [];

  return (
    <div className="flex-1 flex flex-col">
      {/* Header */}
      <div
        className="px-4 py-3 border-b font-semibold"
        style={{
          borderColor: "var(--border-main)",
          color: "var(--text-main)",
        }}
      >
        Chat
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-xs px-4 py-2 rounded-lg text-sm ${
              msg.from === "me"
                ? "ml-auto"
                : "mr-auto"
            }`}
            style={{
              backgroundColor:
                msg.from === "me"
                  ? "var(--accent-primary)"
                  : "var(--bg-input)",
              color:
                msg.from === "me"
                  ? "#020617"
                  : "var(--text-main)",
            }}
          >
            {msg.text}
          </div>
        ))}
      </div>

      {/* Input */}
      <div
        className="p-3 border-t"
        style={{ borderColor: "var(--border-main)" }}
      >
        <input
          type="text"
          placeholder="Type a message..."
          className="w-full px-4 py-2 rounded-md outline-none"
          style={{
            backgroundColor: "var(--bg-input)",
            color: "var(--text-main)",
            border: "1px solid var(--border-input)",
          }}
          disabled
        />
      </div>
    </div>
  );
};

export default ChatDashboard;
